select count(1) != 0 AS exist from public.alteracion_joven

	
		AND codigo_empresa = #{codigo_empresa}
	
	
		AND codigo_sucursal = #{codigo_sucursal}
	
	
		AND ${parameter} like '${value}'
	


	${limit}

select count(1) from public.alteracion_joven j inner join paciente p on (p.codigo_empresa = j.codigo_empresa and p.codigo_sucursal = j.codigo_sucursal and p.nro_identificacion = j.identificacion)
AND j.codigo_empresa = #{codigo_empresa}
AND j.codigo_sucursal = #{codigo_sucursal}
AND to_char(j.fecha_inicial, 'yyyy-MM-dd') like '%${fecha_inicial}%'
AND j.identificacion = #{identificacion}
AND j.tipo_historia = #{tipo_historia}
AND ${parameter} like '${value}'
order by cast(codigo_historia as integer) desc
