select count(1)
from public.hisc_deteccion_alt_menor_2m_2a a
inner join paciente p on
(p.codigo_empresa = a.codigo_empresa
and p.codigo_sucursal =
a.codigo_sucursal and p.nro_identificacion = a.identificacion)

	AND a.codigo_empresa = #{codigo_empresa}
	AND a.codigo_sucursal = #{codigo_sucursal}
	AND a.identificacion = #{identificacion}
	AND to_char(a.fecha_inicial, 'yyyy-MM-dd') like '%${fecha_string}%'
	AND a.tipo_historia = #{tipo_historia}
	AND ${parameter} like '${value}'

order by cast(codigo_historia as integer) desc
