mapper.update:
update public.laboratorios set codigo_empresa = #{codigo_empresa},
codigo_sucursal = #{codigo_sucursal},codigo_procedimiento = #{codigo_procedimiento},
creacion_date = #{creacion_date},ultimo_update = #{ultimo_update},
delete_date = #{delete_date},creacion_user = #{creacion_user},
ultimo_user = #{ultimo_user},delete_user = #{delete_user}
where codigo_empresa = #{codigo_empresa}
and codigo_sucursal = #{codigo_sucursal}
and codigo_procedimiento = #{codigo_procedimiento}

mapper.select:
select codigo_empresa,codigo_sucursal,
codigo_procedimiento,creacion_date,ultimo_update,delete_date,
creacion_user,ultimo_user,delete_user
from public.laboratorios where codigo_empresa = #{codigo_empresa}
and codigo_sucursal = #{codigo_sucursal} and codigo_procedimiento = #{codigo_procedimiento}

mapper.delete:
delete from public.laboratorios
where codigo_empresa = #{codigo_empresa}
and codigo_sucursal = #{codigo_sucursal} and codigo_procedimiento = #{codigo_procedimiento}

mapper.select:
select codigo_empresa,codigo_sucursal,
codigo_procedimiento,creacion_date,ultimo_update,delete_date,
creacion_user,ultimo_user,delete_user
from public.laboratorios 


	AND codigo_empresa = #{codigo_empresa}


	AND codigo_sucursal = #{codigo_sucursal}


	AND ${parameter} like '${value}'



${limit}

